class User {
  final String id;
  final String email;
  final String name;
  final String profilePictureUrl;
  final String? passwordHash;

  User({
    required this.id,
    required this.email,
    required this.name,
    this.profilePictureUrl = 'data:image/png;base64,',
    this.passwordHash,
  } );

  factory User.fromJson(Map<String, dynamic> json) {
    return User(
      id: json['id'],
      email: json['email'],
      name: json['name'],
      profilePictureUrl: json['profilePictureUrl'] ?? 'data:image/png;base64,',
      passwordHash: json['passwordHash'],
    );
  }

  Map<String, dynamic> toJson() {
    return {
      'id': id,
      'email': email,
      'name': name,
      'profilePictureUrl': profilePictureUrl,

    };
  }
}
